import React from 'react';
import "./ChoicesGrid.scss";
import Toiletpaper from "../../../assets/images/toiletpaper.png";
import ChoicesCard from "../ChoiceCard/ChoiceCard";
import Button from "../../Button/Button";
import { useNavigate } from 'react-router-dom';

const ChoicesGrid = ({gameOptions, playersChoice, setPlayersChoice, lives, setLives, productsWithMilk, setResult }) => {
  console.log("playersChoice: ", playersChoice);

  let navigate = useNavigate();
  const redirect = ()=>{
    if(lives > 0){
      navigate("/result")
    } else {
      navigate("/gameover")
    }
  };

  const handleAnswerCheck = () => {
    if(productsWithMilk.includes(playersChoice)){
      setResult("loose");
      setLives(lives-1);
      redirect();
    } else{
      setResult("win")
    }
  };


  return (
    <section>
      <div>
        <div>
          <h2>Das Laktose-Quiz</h2>
          <h2>Wähle das Produkt OHNE Laktose!</h2>
        </div>
        <div>
          <p>Deine verbliebenen Leben:</p>
          {(lives === 3) ? 
            <div>
              <img src={Toiletpaper} alt='Toiletpaper roll' />
              <img src={Toiletpaper} alt='Toiletpaper roll' />
              <img src={Toiletpaper} alt='Toiletpaper roll' />
            </div>
            : (lives === 2) ? 
            <div>
              <img src={Toiletpaper} alt='Toiletpaper roll' />
              <img src={Toiletpaper} alt='Toiletpaper roll' />
            </div>
            : (lives === 1) ?
            <div>
              <img src={Toiletpaper} alt='Toiletpaper roll' />
            </div>
            :<div>
            </div>
            }
        </div>
        <div>
          {gameOptions.map((option, index)=>{
            return(
          <ChoicesCard option={option} index={index} setPlayersChoice={setPlayersChoice}/>
        )})}
        </div>
        <Button text="Überprüfen" action={handleAnswerCheck}/>
      </div>
    </section>
  )
}

export default ChoicesGrid;
